import React, { useState, useEffect } from "react";
//import TranscodeOutput from "./components/Transcoded";
import Message from "./components/Message";
import {fetchVedio} from "./services";
import {initializeTranscoding} from "./services";
import './App.css';

function App() {
  const [userState, setUserState] = useState({

    data: {},
    error: "",
    message : ""

  });
  useEffect(() => {

        setUserState({
       data: useState.data,
       message : userState.message
        });
  }, []);

  const handleSearch = (data ) => {
    setUserState({
      data: data,
    });
  };

  const handleError = (error) => {
    setUserState({
      error: error,
    });
  };

  let content;
  let searchPokemon;
  let pokemonCard;
  let messageContent;



  const performConvertion = () => {
   // userState.message = "Conversion in Progress..."
    userState.error = "";
    initializeTranscoding()
      .then((data) => {
        userState.message = "Conversion completed!"
        userState.error = "";
        userState.data = data;

      })
      .catch((err) => {

      });
  };

  const performFetching = () => {
     userState.message = "Conversion in Progress..."
     userState.error = "";
     fetchVedio()
       .then((data) => {
         userState.message = ""
         userState.error = "";
         userState.data = data;
         console.log(data);

       })
       .catch((err) => {

       });
   };

   //
  if (useState.data) {
    content = (
      <button className="add" onClick={performFetching}>
       show converted vedio
       </button>
    );
  }

  if (userState.message === "") {
    messageContent = <Message message={userState.message} />;
  }
  return (
    <div className="App">
      <button className="add" onClick={performConvertion}>
       Intialize Convertion
      </button>
      {messageContent}
      {content}
    </div>
  );
}

export default App;
