import React, { useState } from "react";
//import { fetchPokemon, fetchfavorites } from "../services";
import messages from "../messages";
import {Button, Card, Container, Row, Col, Modal}from 'react-bootstrap'
import axios from 'axios';
import pic from './pic.png';
import media from './video.jpeg'


const Transcoded = ({ user, onSearch }) => {

  const [error, setError] = useState("");
  const [isLoading, setLoading] = useState(false);
  const [isTranscoded, setTranscode] = useState(false);
  const performSearch = () => {
   setLoading(true)
    setError("");
    // fetchPokemon(convertedName)
    //   .then((data) => {
    //     setError("");
    //     fetchfavorites()
    //       .then((favorites) => {
    //         setPokemonName("");
    //         onSearch(data, user.username, favorites);
    //       })
    //       .catch((err) => {
    //         setError(messages[err.code || "DEFAULT"]);
    //       });
    //   })
    //   .catch((err) => {
    //     setError(messages["INCORRECT_NAME"]);
    //   });

    axios
    .get('https://ysahowljk6.execute-api.us-west-2.amazonaws.com/test/transcode')
    .then((response) => {
        setLoading(false);
        console.log(response);
        setTranscode(true);
        onSearch({"status":"done"})


    })
    .catch((error) => {
        setLoading(false);
        setTranscode(true);
        onSearch({"status":"done"})
      console.log(error);
    });

  };
  let wait;
  let output;
 if(isLoading){
     wait = <p>Your video Transcoding is in Progress!!</p>
 }
 if(isTranscoded){
     console.log("done transcoding")
   output =  <Card className="mb-3" style={{color:"#000"}}>
   <Card.Img src={pic}/>
   <Card.Body>
       <Card.Title>
           Video After Transcoding
       </Card.Title>
   </Card.Body>
</Card>

 }

  return (
    <div className="search">
      <p className="error">{error}</p>
      <p>Hello {user.name} Welcome to MediaZone!!</p>
      <p>Please confirm the email to get the notifications</p>

      <Container>
          <Row>
            <Col>
      <Card className="mb-3" style={{color:"#000"}}>
          <Card.Img src={pic}/>
          <Card.Body>
              <Card.Title>
                  Video before
              </Card.Title>


          </Card.Body>
      </Card>
      </Col>

      {wait}
      <Col>
      {output}
      </Col>
      </Row>
      </Container>
      <div class="col text-center">

<div class="container">
<button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModalCenter">
  Launch demo modal
</button>


<div class="modal fade" id="exampleModalCenter" tabindex="-1" role="dialog" aria-labelledby="exampleModalCenterTitle" aria-hidden="true">
  <div class="modal-dialog modal-dialog-centered" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLongTitle">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>


</div>


      <Button className="button" onClick={performSearch}>Start Transcoding the video</Button>
      </div>
      <section className="about">
          <div className="container p-1 p-sm-3">
          <hr/>
              <div className="row">
                  <div className="col-12 text-center">
                      <h2>About Media Zone</h2>

                  </div>
                  <div className="col-md-3">
                      <img className="img-fluid" src={media}/>

                  </div>
                  <div className="col-9">
                      <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Voluptate doloribus velit eligendi ratione praesentium laborum, dignissimos, quod qui voluptatibus numquam ad, nostrum soluta nulla distinctio natus. Quae inventore eaque nobis!</p>
                  </div>
              </div>
          </div>

      </section>
    </div>
  );
};

export default Transcoded;
