import React, { useState } from "react";
//import { fetchPokemon, fetchfavorites } from "../services";
import messages from "../messages";
import { Button, Card, Container, Row, Col, Modal } from "react-bootstrap";
import axios from "axios";
import pic from "./pic.png";
import media from "./video.jpeg";

const Transcoded = ({ user, onSearch }) => {
  const [error, setError] = useState("");
  const [isLoading, setLoading] = useState(false);
  const [isTranscoded, setTranscode] = useState(false);
  const [showHide, setShowHide] = useState(false);
  const [userEmail, setEmail] = useState("");

  const sendSnS = () =>{

    axios
      .post("https://f3l7h6tnse.execute-api.us-west-2.amazonaws.com/test", {
        email: userEmail,
      })
      .then((response) => {
        console.log(userEmail);
      })
      .catch((error) => {
        console.log(userEmail);
        console.log(error);
      });
  }

  const performSearch = () => {
    setLoading(true);
    setShowHide(!showHide)
    setError("");
    // fetchPokemon(convertedName)
    //   .then((data) => {
    //     setError("");
    //     fetchfavorites()
    //       .then((favorites) => {
    //         setPokemonName("");
    //         onSearch(data, user.username, favorites);
    //       })
    //       .catch((err) => {
    //         setError(messages[err.code || "DEFAULT"]);
    //       });
    //   })
    //   .catch((err) => {
    //     setError(messages["INCORRECT_NAME"]);
    //   });

    if(userEmail != ""){
        const re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
        if (!re.test(String(userEmail).toLowerCase())) {
          setError(messages.USERNAME_REQUIRED);
          return;
        }
        sendSnS();
    }
    axios
      .get(
        "https://ysahowljk6.execute-api.us-west-2.amazonaws.com/test/transcode"
      )
      .then((response) => {
        setLoading(false);
        console.log(response);
        setTranscode(true);
        onSearch({ status: "done" });
      })
      .catch((error) => {
        setLoading(false);
        setTranscode(true);
        onSearch({ status: "done" });
        console.log(error);
      });
  };
  let wait;
  let output;
  if (isLoading) {
    wait = <p>Your video Transcoding is in Progress!!</p>;
  }
  if (isTranscoded) {
    console.log("done transcoding");
    output = (
      <Card className="mb-3" style={{ color: "#000" }}>
        <Card.Img src={pic} />
        <Card.Body>
          <Card.Title>Video After Transcoding</Card.Title>
        </Card.Body>
      </Card>
    );
  }

  return (
    <div className="search">
      <p className="error">{error}</p>
      <p>Hello {user.name} Welcome to MediaZone!!</p>
      <p>Please confirm the email to get the notifications</p>

      <Container>
        <Row>
          <Col>
            <Card className="mb-3" style={{ color: "#000" }}>
              <Card.Img src={pic} />
              <Card.Body>
                <Card.Title>Video before</Card.Title>
              </Card.Body>
            </Card>
          </Col>

          {wait}
          <Col>{output}</Col>
        </Row>
      </Container>
      <div class="col text-center">
        <div>
          <Button variant="primary" onClick={() => setShowHide(!showHide)}>
            Launch demo modal
          </Button>

          <Modal show={showHide}>
            <Modal.Header closeButton onClick={() => setShowHide(!showHide)}>
              <Modal.Title>Hi There</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            Would you like to recieve notification about you Transcoding task?
            <label> If yes, Please enter your email:</label>
            <input
              type="text"
              className="form-control"
              onChange={(e) => setEmail(e.target.value)}
                  placeholder="joseagnesria@gmail.com"
            />

            </Modal.Body>
            <Modal.Footer>
              {/* <Button variant="secondary" onClick={() => setShowHide(!showHide)}>
                Transcode without subscription
              </Button> */}
              <Button variant="primary" onClick={performSearch}>
              Transcode
              </Button>
            </Modal.Footer>
          </Modal>
        </div>

        {/* <Button className="button" onClick={performSearch}>
          Start Transcoding the video
        </Button> */}
      </div>
      <section className="about">
        <div className="container p-1 p-sm-3">
          <hr />
          <div className="row">
            <div className="col-12 text-center">
              <h2>About Media Zone</h2>
            </div>
            <div className="col-md-3">
              <img className="img-fluid" src={media} />
            </div>
            <div className="col-9">
              <p>
                Lorem, ipsum dolor sit amet consectetur adipisicing elit.
                Voluptate doloribus velit eligendi ratione praesentium laborum,
                dignissimos, quod qui voluptatibus numquam ad, nostrum soluta
                nulla distinctio natus. Quae inventore eaque nobis!
              </p>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Transcoded;
