import React, { useState, useEffect } from "react";
import { fetchLogin } from "../services";
import messages from "../messages";
import {Button , Container, Col, Row, Form, FormGroup}from 'react-bootstrap'
import axios from 'axios';
import media from './video.jpeg'

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("joseagnesria@gmail.com");
  const [name, setName] = useState("");
  const [error, setError] = useState("");
  //const [isLoading, setLoading] = useState(true);




  const performLogin = (e) => {
      e.preventDefault();
    const re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    if (!username || !re.test(String(username).toLowerCase())) {
      setError(messages.USERNAME_REQUIRED);
      return;
    }
    if(!name){
        setError(messages.NAME_REQUIRED);
        return; 
    }
    setError("");
    axios
    .post('https://f3l7h6tnse.execute-api.us-west-2.amazonaws.com/test', {"email":username})
    .then((response) => {
        onLogin(username);
      console.log(username);
    })
    .catch((error) => {
        console.log(username);
        onLogin(username,name);
      console.log(error);
    });
  }

    // fetchLogin(username)
    //   .then((userInfo) => {
    //     setUsername("");
    //     onLogin(username);
    //   })
    //   .catch((err) => {
    //     setUsername("");
    //     //setError(messages[err.code || "DEFAULT"]);
    //   });
  //};

  return (
    <div className="login">    
      <p className="error">{error}</p>
      <Container>
          <Form>
              <Row>
                  <Col md>
                      <Form.Group controlId="exampleForm.ControlTextarea1">
                          <Form.Label>Name</Form.Label>

                          <Form.Control onChange={(e) => setName(e.target.value)} placeholder="ria"/>

                      </Form.Group>
                      </Col>

                      <Col md>
                      <Form.Group controlId="formEmail">
                          <Form.Label>Email Address</Form.Label>

                          <Form.Control type="email"  onChange={(e) => setUsername(e.target.value)} placeholder="joseagnesria@gmail.com"/>
                        <Form.Text className="Text-muted">
                            You can unsubscribe anytime you want
                        </Form.Text>

                      </Form.Group>
                      </Col>
                      <Col md>
                      <Button varient="secondary" type = "submit" onClick={(e) => (performLogin(e))}>Subscribe</Button>
                      </Col>
                      </Row>

      {/* <input
        size="40"
        onChange={(e) => setName(e.target.value)}
        placeholder="Enter you Name"
        value={name}
      /> */}

      {/* <input
        size="40"
        onChange={(e) => setUsername(e.target.value)}
        placeholder="Enter you Email"
        value={username}
      /> */}

        {/* <Button className="button" onClick={performLogin}>
          Subscribe
        </Button> */}


      </Form>
      </Container>

      <section className="about">
          <div className="container p-1 p-sm-3">
          <hr/>
              <div className="row">
                  <div className="col-12 text-center">
                      <h2>About Media Zone</h2>

                  </div>
                  <div className="col-md-3">
                      <img className="img-fluid" src={media}/>

                  </div>
                  <div className="col-9"></div>
              </div>
          </div>

      </section>
    </div>
  );
};

export default Login;
