import React, { useState } from "react";
import { fetchLogin } from "../services";
import messages from "../messages";
import axios from 'axios';

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("joseagnesria@gmail.com");
  const [name, setName] = useState("");
  const [error, setError] = useState("");

  const performLogin = () => {
    const re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    if (!username || !re.test(String(username).toLowerCase())) {
      setError(messages.USERNAME_REQUIRED);
      return;
    }
    if(!name){
        setError(messages.NAME_REQUIRED);
        return; 
    }
    setError("");
    axios
    .post('https://f3l7h6tnse.execute-api.us-west-2.amazonaws.com/test', {"email":username})
    .then((response) => {
        onLogin(username);
      console.log(username);
    })
    .catch((error) => {
        console.log(username);
        onLogin(username,name);
      console.log(error);
    });
  }

    // fetchLogin(username)
    //   .then((userInfo) => {
    //     setUsername("");
    //     onLogin(username);
    //   })
    //   .catch((err) => {
    //     setUsername("");
    //     //setError(messages[err.code || "DEFAULT"]);
    //   });
  //};

  return (
    <div className="login">



       <video controls="" autoplay="" name="media"><source src="https://dw00dsohded4a.cloudfront.net/destination%20folder/songoutput1.mov" type="video/mp4"/>
       </video>

      <p className="error">{error}</p>
      <input
        size="40"
        onChange={(e) => setName(e.target.value)}
        placeholder="Enter you Name"
        value={name}
      />
      <input
        size="40"
        onChange={(e) => setUsername(e.target.value)}
        placeholder="Enter you Email"
        value={username}
      />
      {
        <button className="button" onClick={performLogin}>
          Subscribe
        </button>
      }
    </div>
  );
};

export default Login;
