import React, { useState } from "react";
//import { fetchPokemon, fetchfavorites } from "../services";
import messages from "../messages";
import { Player } from 'video-react';
import axios from 'axios';
import pic from './pic.png';


const Transcoded = ({ user, onSearch }) => {

  const [error, setError] = useState("");
  const [isLoading, setLoading] = useState(false);
  const [isTranscoded, setTranscode] = useState(false);
  const performSearch = () => {
   setLoading(true)
    setError("");
    // fetchPokemon(convertedName)
    //   .then((data) => {
    //     setError("");
    //     fetchfavorites()
    //       .then((favorites) => {
    //         setPokemonName("");
    //         onSearch(data, user.username, favorites);
    //       })
    //       .catch((err) => {
    //         setError(messages[err.code || "DEFAULT"]);
    //       });
    //   })
    //   .catch((err) => {
    //     setError(messages["INCORRECT_NAME"]);
    //   });

    axios
    .get('https://ysahowljk6.execute-api.us-west-2.amazonaws.com/test/transcode')
    .then((response) => {
        setLoading(false);
        console.log(response);
        setTranscode(true);
        onSearch({"status":"done"})


    })
    .catch((error) => {

      console.log(error);
    });

  };
  let wait;
  let output;
 if(isLoading){
     wait = <p>Transcoding ...</p>
 }
 if(isTranscoded){
   output =  <img className="image" alt="pokemon" src={pic} />
 }

  return (
    <div className="search">
      <p className="error">{error}</p>
      <p>Hello {user.name} Welcome to MediaZone!!</p>
      <p>Please confirm the email to get the notifications</p>
      <img className="image" alt="pokemon" src={pic} />
      <button className="button" onClick={performSearch}>
        Transcode
      </button>
      {wait}
      {output}
    </div>
  );
};

export default Transcoded;
